/**
 * A helper to test Redux connected components by passing a mocked Redux store.
 * @param {Object} mockState - A mock Redux state.
 * @return {Object} A mocked Redux store.
 */
export const mockStore = mockState => ({
  dispatch: () => {},
  getState: () => mockState,
  subscribe: () => {},
});

export default { mockStore };

import createSagaMiddleware from 'redux-saga';
import storage from 'redux-persist/lib/storage';
import { all } from 'redux-saga/effects';
import { combineReducers, createStore, applyMiddleware } from 'redux';
import { persistStore, persistReducer } from 'redux-persist';

import settings from './settings';
import ui from './ui';

/**
 * The Redux Saga middleware.
 * @type {Object}
 */
const sagaMiddleware = createSagaMiddleware();

/**
 * The Redux store.
 * @type {Object}
 */
const store = createStore(
  persistReducer({
    key: 'settings',
    version: 1,
    whitelist: ['settings'],
    storage,
  }, combineReducers({
    settings: settings.reducer,
    ui: ui.reducer,
  })),
  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__(),
  applyMiddleware(sagaMiddleware),
);

sagaMiddleware.run(function* rootSaga() {
  yield all([
    ...settings.sagas,
    ...ui.sagas,
  ]);
});

/**
 * The persistor object from redux-persist.
 * @type {Object}
 */
export const persistor = persistStore(store);

export default store;

/**
 * This function sends an action to store the new data to {@link settingsReducer}.
 * @param {Object} data - An object with the key/value pair to store in the store.
 * @return {Object} Redux action
 */
export const storeSettings = data => ({ type: 'STORE_SETTINGS', data });

/**
 * This function sends an action to be intercepted by {@link watchUpdateSettings}.
 * @param {Object} data - An object with the key/value pair to update in the store.
 * @return {Object} Redux action
 */
export const updateSettings = data => ({ type: 'UPDATE_SETTINGS', data });

import * as actions from './actions';
import config from '../../../config';
import reducer from './reducer';
import sagas from './sagas';

/**
 * The initial state of the Settings Redux store.
 * @type {Object}
 * @property {string} lang=en The page language.
 * @property {string} theme=dark The color theme used.
 */
export const settingsDefaultState = {
  lang: config.defaultLang,
  theme: 'dark',
};

export default {
  actions,
  reducer,
  sagas,
  settingsDefaultState,
};

import _ from 'lodash';
import { settingsDefaultState } from '../';

/**
 * The Settings reducer.
 * @param {Object} [state=settingsDefaultState] - Redux state
 * @param {Object} action - Redux action
 * @return {Object} Updated Redux state
 */
export default function settingsReducer(state = settingsDefaultState, action) {
  switch (_.get(action, 'type')) {
    case 'STORE_SETTINGS':
      return {
        ...state,
        ...action.data,
      };
    default:
      return state;
  }
}

import _ from 'lodash';
import { put, takeEvery, fork } from 'redux-saga/effects';

import { storeSettings } from '../actions';

/**
 * This generator function stores the action data yielded from {@link watchUpdateSettings}.
 * @param {Object} action Redux action
 * @emits {storeSettings}
 */
export function* updateSettingsSaga(action) {
  yield put(storeSettings(_.get(action, 'data')));
}

/**
 * This generator function watchs for every {@link updateSettings} call.
 * @emits {updateSettingsSaga}
 */
export function* watchUpdateSettings() {
  yield takeEvery('UPDATE_SETTINGS', updateSettingsSaga);
}

export default [
  fork(watchUpdateSettings),
];

