import * as actions from './actions';
import reducer from './reducer';
import sagas from './sagas';

/**
 * The initial state of the UI Redux store.
 * @type {Object}
 * @property {boolean} sidebarOpen=false Whether or not the sidebar is open.
 * @property {Object} metrics Object of layout metric variables.
 * @property {number} metrics.headerHeight=72 Height of header.
 * @property {number} metrics.windowWidth=0 Width of widnow.
 */
export const uiDefaultState = {
  sidebarOpen: false,
  metrics: {
    headerHeight: 72,
    windowWidth: 0,
  },
};


export default {
  actions,
  reducer,
  sagas,
  uiDefaultState,
};

import _ from 'lodash';
import { uiDefaultState } from '../';

/**
 * The UI reducer.
 * @param {Object} [state=uiDefaultState] - Redux state
 * @param {Object} action - Redux action
 * @return {Object} Updated Redux state
 */
export default function uiReducer(state = uiDefaultState, action) {
  switch (_.get(action, 'type')) {
    case 'STORE_METRIC':
      return {
        ...state,
        metrics: {
          ...state.metrics,
          ...action.data,
        },
      };
    case 'STORE_UI':
      return {
        ...state,
        ...action.data,
      };
    default:
      return state;
  }
}

import _ from 'lodash';
import { fork, put, takeEvery, takeLatest } from 'redux-saga/effects';

import { storeMetric, storeUi } from '../actions';

/**
 * This generator function stores the action data yielded from {@link watchUpdateMetric}.
 * @param {Object} action Redux action
 * @emits {storeMetric}
 */
export function* updateMetricSaga(action) {
  yield put(storeMetric(_.get(action, 'data')));
}

/**
 * This generator function stores the action data yielded from {@link watchUpdateUi}.
 * @param {Object} action Redux action
 * @emits {storeUi}
 */
export function* updateUiSaga(action) {
  yield put(storeUi(_.get(action, 'data')));
}

/**
 * This generator function watchs for the latest {@link updateMetric} call.
 * @emits {updateMetricSaga}
 */
export function* watchUpdateMetric() {
  yield takeLatest('UPDATE_METRIC', updateMetricSaga);
}

/**
 * This generator function watchs for every {@link updateUi} call.
 * @emits {updateUiSaga}
 */
export function* watchUpdateUi() {
  yield takeEvery('UPDATE_UI', updateUiSaga);
}

export default [
  fork(watchUpdateMetric),
  fork(watchUpdateUi),
];
